sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	try{
		gs.info("Business rule for getting auth token executed.");

		var instanceURL = gs.getProperty('glide.servlet.uri');

		var username = current.getElement("username").toString();
		var password = current.getElement("password").getDecryptedValue();
		var client_id = current.oauth_registry.client_id.toString();
		var client_sec = current.oauth_registry.client_secret.getDecryptedValue();

		var r = new sn_ws.RESTMessageV2();
		r.setHttpMethod("post");
		r.setEndpoint(instanceURL+"oauth_token.do");
		r.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");

		var body = 
			"grant_type=password" +
			"&username=" + encodeURIComponent(username) +
			"&password=" + encodeURIComponent(password) +
			"&client_id=" + encodeURIComponent(client_id) +
			"&client_secret=" + encodeURIComponent(client_sec);		
		r.setRequestBody(body);

		var response = r.executeAsync();
		var responseBody = JSON.parse(response.getBody());
		var httpStatus = response.getStatusCode();

		if(httpStatus == '200'){
			gs.info("Token generated successfully!");
			current.setValue("auth_token",responseBody.access_token.toString());
			current.update();
			gs.addInfoMessage("Token created successfully!");
		}

		else {
			gs.error("Error occured in generating auth token: "+JSON.stringify(responseBody));
			current.deleteRecord();
			gs.addErrorMessage("Unable to generate token! Please see application log for more reference.");

		}


	}
	catch(error){
		gs.error("Keeper Security: Unable to receive token "+ error);
		gs.addErrorMessage("Unable to generate token! Please see application log for more reference.");

	}

	// Add your code here

})(current, previous);
