sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

    if (source.app_uid || source.device_name || source.app_client_type || source.record_uid || source.channel) {

        var ext = new GlideRecord('x_keese_keeper_sec_keeper_vaults_alerts_reference');
        ext.initialize();

        ext.sir_reference = target.sys_id;
        ext.app_uid = source.app_uid;
        ext.device_name = source.device_name;
        ext.app_client_type = source.app_client_type;
        ext.record_uid = source.record_uid;
		ext.channel = source.channel;
        ext.insert();
    }

})(source, map, log, target);
