sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    // implement resource here
	var log_template = "[Keeper Vault Alerts]: ";
	var import_set_table = "x_keese_keeper_sec_keeper_vault_alerts_import_table";

	try {
		var payload = request.body.data;	
        gs.info(log_template+"Received payload: " + JSON.stringify(payload));
		gs.info(log_template+"Creating glide record for set import table");

		var gr = new GlideRecordSecure(import_set_table);

		gr.username = payload.username;
		gr.alert_name = payload.alert_name;
		gr.description = payload.description;
		gr.audit_event = payload.audit_event;
		gr.remote_address = payload.remote_address;
		var time_stamp = new Date(payload.timestamp);
		gr.timestamp = time_stamp.toISOString().replace("T"," ").replace("Z","");
		gr.category = payload.category;
		gr.description = payload.description;
		gr.client_version = payload.client_version;

		if (payload.app_uid){
			gr.app_uid = payload.app_uid;
		}

		if (payload.device_name){
			gr.device_name = payload.device_name;
		}

		if (payload.app_client_type){
			gr.app_client_type = payload.app_client_type;
		}

		if (payload.record_uid){
			gr.record_uid = payload.record_uid;
		}

		if (payload.channel){
			gr.channel = payload.channel;
		}

		gr.insert();

		gs.info(log_template+"Import set table updated successfully");

        response.setStatus(200);
        response.setBody({ status: "Alerts imported to ServiceNow successfully." });

    } catch (e) {
        gs.error(log_template + "Error: " + e.message);
        response.setStatus(500);
        response.setBody({ error: "Internal Server Error" });
    }

})(request, response);
